-- subscription
DELIMITER //
CREATE PROCEDURE subscription(IN input_id INT)
BEGIN
    select plan as result from AccountSubscription where user_id = input_id limit 1;
END //
DELIMITER ;

-- savings
DELIMITER //
CREATE PROCEDURE savings(IN input_id INT)
BEGIN
    select concat('$', amount) as result from TotalSavings where user_id = input_id;
END //
DELIMITER ;

-- e-cash
DELIMITER //
CREATE PROCEDURE ecash(IN input_id INT)
BEGIN
    select concat('$', amount) as result from ECash where user_id = input_id;
END //
DELIMITER ;


-- orders num
DELIMITER //
CREATE PROCEDURE ordersNum(IN input_id INT)
BEGIN
    select concat(c1.c, ' COMPLETED orders and ', c2.c, ' CANCELED orders') as result from 
(select count(*) as c from ShoppingOrder where user_id = input_id and status = 'COMPLETED') c1,
(select count(*) as c from ShoppingOrder where user_id = input_id and status = 'CANCEL') c2;
END //
DELIMITER ;

-- completed orders num
DELIMITER //
CREATE PROCEDURE completedOrdersNum(IN input_id INT)
BEGIN
    select concat(count(*), ' orders') as result from ShoppingOrder where user_id = input_id and status = 'COMPLETED';
END //
DELIMITER ;

-- canceled orders num
DELIMITER //
CREATE PROCEDURE canceledOrdersNum(IN input_id INT)
BEGIN
    select concat(count(*), ' orders') as result from ShoppingOrder where user_id = input_id and status = 'CANCEL';
END //
DELIMITER ;

-- order templates=
DELIMITER //
CREATE PROCEDURE templates(IN input_id INT)
BEGIN
    select count(*) as result from OrderTemplate where user_id = input_id;
END //
DELIMITER ;

-- most ordered templates
DELIMITER //
CREATE PROCEDURE frequentTemplate(IN input_id INT)
BEGIN
    select concat(name, ' with ', count(order_id), ' orders') as result from 
(select * from OrderTemplate where user_id = input_id) o
join TemplateToOrder t on o.id = t.template_id
group by template_id, name order by count(order_id) desc limit 1;
END //
DELIMITER ;


-- top frequently purchased products
DELIMITER //
CREATE PROCEDURE frequentlyProducts(IN input_id INT, IN input_limit INT)
BEGIN
select concat(freq, ' orders - ', p.id, ' - ', name) as result from (select product_id, count(*) as freq from 
(select * from ShoppingOrder where user_id = input_id and status <> 'CANCEL') o
join OrderItem i on o.id = i.order_id
group by product_id order by freq desc limit input_limit) i
join Product p where p.id = i.product_id;
END //
DELIMITER ;

-- top recently purchased products
DELIMITER //
CREATE PROCEDURE recentlyProducts(IN input_id INT, IN input_limit INT)
BEGIN
    select concat(
CASE
    WHEN DATEDIFF(now(), recent) > 0 THEN concat(DATEDIFF(now(), recent), ' days ago')
    WHEN TIMESTAMPDIFF(HOUR, recent, now()) > 0 THEN concat(TIMESTAMPDIFF(HOUR, recent, now()), ' hours ago')
    ELSE concat(TIMESTAMPDIFF(MINUTE, recent, now()), ' min ago')
END, 
' - ', p.id, ' - ', name) as result from (select product_id, max(placed_date) as recent from 
(select * from ShoppingOrder where user_id = input_id and status <> 'CANCEL') o
join OrderItem i on o.id = i.order_id
group by product_id order by recent desc limit input_limit) i
join Product p where p.id = i.product_id;
END //
DELIMITER ;




-- num users
DELIMITER //
CREATE PROCEDURE usersNum()
BEGIN
    select count(*) as result from User where role <> 'ADMIN';
END //
DELIMITER ;

-- ordered users rate
DELIMITER //
CREATE PROCEDURE orderedUsersRate()
BEGIN
    select concat(round(100 * c1.c / c2.c, 0), '%') as result from 
(select count(distinct user_id) as c from ShoppingOrder) c1,
(select count(*) as c from User where role <> 'ADMIN') c2;
END //
DELIMITER ;


-- subscribed users rate
DELIMITER //
CREATE PROCEDURE subscribedUsersRate()
BEGIN
    select concat(c1.c, ' subscribed users, rate ', round(100 * c1.c / c2.c, 0), '%') as result from 
(select count(*) as c from AccountSubscription) c1,
(select count(*) as c from User where role <> 'ADMIN') c2;
END //
DELIMITER ;


-- 
DELIMITER //
CREATE PROCEDURE subscriptionNums()
BEGIN
    select concat(c1.c, ' PREMIUM and ', c2.c, ' BASIC') as result from 
(select count(*) as c from AccountSubscription where plan = 'PREMIUM') c1,
(select count(*) as c from AccountSubscription where plan = 'BASIC') c2;
END //
DELIMITER ;


-- subscription profit
DELIMITER //
CREATE PROCEDURE subscriptionProfit()
BEGIN
    select concat('$', round(sum(price), 2)) as result from AccountSubscription s
join SubscriptionPlan p on p.plan = s.plan;
END //
DELIMITER ;


-- selling profit
DELIMITER //
CREATE PROCEDURE sellingProfit()
BEGIN
    select concat('$', round(sum(profit), 2)) as result from ShoppingOrder;
END //
DELIMITER ;

-- orders by statuses
DELIMITER //
CREATE PROCEDURE ordersByStatus()
BEGIN
    select concat(c1.c, ' COMPLETED and ', c2.c, ' CANCELED') as result from 
(select count(*) as c from ShoppingOrder where status = 'COMPLETED') c1,
(select count(*) as c from ShoppingOrder where status = 'CANCEL') c2;
END //
DELIMITER ;


-- num categories
DELIMITER //
CREATE PROCEDURE categoriesNum()
BEGIN
    select concat(count(*), ' categories') as result from Category;
END //
DELIMITER ;

-- num brands
DELIMITER //
CREATE PROCEDURE brandsNum()
BEGIN
    select concat(count(*), ' brands') as result from Brand;
END //
DELIMITER ;


-- num products
DELIMITER //
CREATE PROCEDURE productsNum()
BEGIN
    select concat(c1.c, ' in stock and ', c2.c - c1.c, ' out stock') as result from 
(select count(*) as c from Product where quantity > 0) c1,
(select count(*) as c from Product) c2;
END //
DELIMITER ;


-- sold products
DELIMITER //
CREATE PROCEDURE soldProducts()
BEGIN
    select concat(c1.c, ' sold products, rate ', round(100 * c1.c / c2.c, 0), '%') as result from 
(select count(distinct product_id) as c from OrderItem where is_completed = true) c1,
(select count(*) as c from Product) c2;
END //
DELIMITER ;

-- top profit
DELIMITER //
CREATE PROCEDURE topProfit(IN input_limit INT)
BEGIN
    select concat('$', profit, ' - ', p.id, ' - ', name) as result from 
(select product_id, round(sum(quantity * (purchased_price - wholesale_price)), 2) as profit from OrderItem where is_completed = true
group by product_id order by profit desc limit input_limit) i
join Product p on i.product_id = p.id;
END //
DELIMITER ;


-- top popular
DELIMITER //
CREATE PROCEDURE topPopular(IN input_limit INT)
BEGIN
    select concat(freq, ' Qty - ', p.id, ' - ', name) as result from 
(select product_id, sum(quantity) as freq from OrderItem where is_completed = true
group by product_id order by freq desc limit input_limit) i
join Product p on i.product_id = p.id;
END //
DELIMITER ;



